<?phpclass Application_Model_BackTabulationReportItems extends Zend_Db_Table_Abstract{    public $_name = 'back_tabulation_report_items';    protected $_id = 'id';      //get details by record for edit	public function getRecord($id)    {               $select=$this->_db->select()                      ->from($this->_name)                      ->where("$this->_name.$this->_id=?", $id)				   					  ->where("$this->_name.status !=?", 2);        $result=$this->getAdapter()                      ->fetchRow($select);               return $result;    }		//Get all records	public function getRecords()    {               $select=$this->_db->select()                      ->from($this->_name) 					  ->where("$this->_name.status !=?", 2)					  ->order("$this->_name.$this->_id DESC");        $result=$this->getAdapter()                      ->fetchAll($select);               return $result;    }	}