<?phpclass Application_Model_Session extends Zend_Db_Table_Abstract {    public $_name = 'session_info';    protected $_id = 'id';    public function getRecord($id) {        $select = $this->_db->select()                ->from($this->_name)                ->where("$this->_name.$this->_id =?", $id);               // ->where("status=?",0);        $result = $this->getAdapter()                ->fetchRow($select);        return $result;    }    public function getSessionRecordByYearId($year_id){        $select = $this->_db->select()                ->from($this->_name)             ->where('acad_year_id=?',$year_id);        $result = $this->getAdapter()                ->fetchAll($select);        return $result;    }        public function getRecords() {        $select = $this->_db->select()                ->from($this->_name)                       ->joinleft(array("academic_year"),"academic_year.year_id=$this->_name.acad_year_id");        $result = $this->getAdapter()                ->fetchAll($select);        return $result;    }    public function getSession($name) {        $select = $this->_db->select()                ->from($this->_name)                ->where('session=?',$name);        $result = $this->getAdapter()                ->fetchAll($select);        return $result;    }            public function getActiveRecords() {        $select = $this->_db->select()                ->from($this->_name)                ->where('status=?',0);                    $result = $this->getAdapter()                ->fetchAll($select);        return $result;    }            	public function getDropDownList(){        $select = $this->_db->select()		->from($this->_name, array('id','session'))								->where("status =?",0)                ->order('id  ASC');        $result = $this->getAdapter()->fetchAll($select);        $data = array();		$st_year ='';		$end_year='';        foreach ($result as $val) {						$data[$val['id']] = $val['session'];			           // $data[$val['academic_id']] = substr($val['from_date']).'-'.substr($val['to_date']);			//print_r($data);die;        }        return $data;    }                }